// Define the expected topic
const expectedTopic = "b5/316/platz/lula";
const exceptedUrl = "/api/platz/lula";

if (!msg.payload) {
    return [
        { payload: "ERROR: Payload is missing." }, // MQTT/Error message to output 1
        { 
            payload: { error: "Payload is missing. Expected 'True' or 'False'." },
            statusCode: 400,
            headers: { "Content-Type": "application/json" }, // Ensures proper HTTP response
            res: msg.res
        }
    ];
}

let responseMsg = { res: msg.res, headers: { "Content-Type": "application/json" } };

// Ensure the correct API endpoint
if (msg.req.url === exceptedUrl) {
    let outputMessage = { payload: msg.payload, topic: expectedTopic };

    // Check if the payload is valid
    if (msg.payload === "True" || msg.payload === "False") {
        // Send a valid message with both values
        return [
            outputMessage,  // Output 1 (valid MQTT message)
            { statusCode: 200, payload: { success: msg.payload }, res: msg.res } // Output 2 (valid HTTP response)
        ];
    } else {
        // Invalid payload: Send error response + message to MQTT
        responseMsg.payload = { error: "Invalid Payload. Expected: 'True' or 'False'." };
        responseMsg.statusCode = 400;

        return [
            { payload: "ERROR: Invalid payload." }, // Output 1 (MQTT/Error message)
            responseMsg // Output 2 (HTTP error response)
        ];
    }
}

// Invalid URL response
responseMsg.payload = { error: "Invalid URL. Expected:"+exceptedUrl };
responseMsg.statusCode = 404;
return [
    { payload: "ERROR: Invalid URL." }, // Output 1 (MQTT/Error message)
    responseMsg // Output 2 (HTTP error response)
];

