let keyValuesToDelete = msg.req.body || {}; // Key-value pairs provided in the DELETE request
let deletedKeys = [];

// Function to delete keys only if value matches (supports nested keys)
function deleteMatchingKeys(obj, key, value) {
    if (obj.hasOwnProperty(key) && obj[key] === value) {
        delete obj[key];
        return true;
    }
    for (let k in obj) {
        if (typeof obj[k] === "object" && obj[k] !== null) {
            if (deleteMatchingKeys(obj[k], key, value)) return true;
        }
    }
    return false;
}

// Try deleting each key-value pair from the JSON object
Object.entries(keyValuesToDelete).forEach(([key, value]) => {
    if (deleteMatchingKeys(data, key, value)) {
        deletedKeys.push({ key, value });
    }
});

// If no keys were deleted, return an error message
if (deletedKeys.length === 0) {
    msg.payload = { 
        error: "No matching key-value pairs found to delete", 
        provided: keyValuesToDelete 
    };
} else {
    msg.payload = {
        message: "Deleted matching key-value pairs successfully",
        deletedKeys: deletedKeys,
        remainingData: data
    };
}

// Convert back to JSON string for writing to file
msg.payload = JSON.stringify(data, null, 2);
return msg;

