// Funktion zum tiefen Aktualisieren von JSON-Daten (auch für verschachtelte Keys)
function deepMerge(target, source) {
    for (const key in source) {
        if (typeof source[key] === 'object' && source[key] !== null && !Array.isArray(source[key])) {
            if (!target[key] || typeof target[key] !== 'object') {
                target[key] = {};  // Falls das Ziel keinen passenden Key hat, wird ein Objekt erstellt
            }
            deepMerge(target[key], source[key]);  // Rekursive Aktualisierung
        } else {
            target[key] = source[key];  // Wert direkt aktualisieren
        }
    }
}

